import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const App = () => {
    const [cveList, setCveList] = useState([]);
    const [totalRecords, setTotalRecords] = useState(0);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [resultsPerPage, setResultsPerPage] = useState(10);
    const [currentPage, setCurrentPage] = useState(0);
    const [sortOrder, setSortOrder] = useState('desc');

    const fetchCveData = async () => {
        setIsLoading(true);
        try {
            const response = await fetch(
                `http://localhost:5000/api/cves?resultsPerPage=${resultsPerPage}&startIndex=${currentPage *
                    resultsPerPage}&sortBy=publishedDate&sortOrder=${sortOrder}`
            );
            if (!response.ok) {
                throw new Error(`API call failed with status ${response.status}`);
            }
            const data = await response.json();
            setCveList(data.vulnerabilities || []);
            setTotalRecords(data.totalResults || 0);
        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        fetchCveData();
    }, [resultsPerPage, currentPage, sortOrder]);

    const handleRowClick = (cveId) => {
        window.location.href = `/cves/${cveId}`;
    };

    return (
        <div className="container mt-4">
            <h1 className="text-center mb-4">CVE List</h1>
            <div className="mb-3">
                <strong>Total Records:</strong> {totalRecords}
            </div>
            <div className="row mb-3 align-items-center">
                <div className="col-md-6">
                    <label className="form-label">
                        Results Per Page:
                        <select
                            className="form-select"
                            value={resultsPerPage}
                            onChange={(e) => setResultsPerPage(Number(e.target.value))}
                        >
                            <option value={10}>10</option>
                            <option value={50}>50</option>
                            <option value={100}>100</option>
                        </select>
                    </label>
                </div>
                <div className="col-md-6 text-end">
                    <label className="form-label">
                        Sort Order:
                        <select
                            className="form-select d-inline-block w-auto ms-2"
                            value={sortOrder}
                            onChange={(e) => setSortOrder(e.target.value)}
                        >
                            <option value="asc">Ascending</option>
                            <option value="desc">Descending</option>
                        </select>
                    </label>
                </div>
            </div>
            {isLoading ? (
                <div className="text-center">
                    <div className="spinner-border" role="status">
                        <span className="visually-hidden">Loading...</span>
                    </div>
                </div>
            ) : error ? (
                <div className="alert alert-danger" role="alert">
                    Error: {error}
                </div>
            ) : cveList.length > 0 ? (
                <div className="table-responsive">
                    <table className="table table-bordered table-hover">
                        <thead className="table-dark">
                            <tr>
                                <th>CVE ID</th>
                                <th>Description</th>
                                <th>Published Date</th>
                                <th>Last Modified</th>
                            </tr>
                        </thead>
                        <tbody>
                            {cveList.map((cve) => (
                                <tr key={cve.cve.id} onClick={() => handleRowClick(cve.cve.id)} style={{ cursor: 'pointer' }}>
                                    <td>{cve.cve.id}</td>
                                    <td>{cve.cve.descriptions[0].value}</td>
                                    <td>{new Date(cve.cve.published).toLocaleDateString()}</td>
                                    <td>{new Date(cve.cve.lastModified).toLocaleDateString()}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            ) : (
                <div className="alert alert-warning" role="alert">
                    No data available...
                </div>
            )}
            <div className="d-flex justify-content-between mt-3">
                <button
                    className="btn btn-primary"
                    disabled={currentPage === 0}
                    onClick={() => setCurrentPage((prev) => prev - 1)}
                >
                    Previous
                </button>
                <button
                    className="btn btn-primary"
                    disabled={(currentPage + 1) * resultsPerPage >= totalRecords}
                    onClick={() => setCurrentPage((prev) => prev + 1)}
                >
                    Next
                </button>
            </div>
        </div>
    );
};

export default App;
